(function ($) {
    "use strict";

    const extra = {
        nepaliClock: function () {
            const currentTime = new Date();
            let hours = currentTime.getHours();
            let minutes = currentTime.getMinutes();
            let seconds = currentTime.getSeconds();
            let ampm = "विहानको";
            if (hours > 12) {
                hours = hours - 12;
                ampm = "अपराह्नको";
            } else if (hours === 0) {
                hours = 12;
            }
            if (minutes < 10) {
                minutes = "0" + minutes;
            }
            if (seconds < 10) {
                seconds = "0" + seconds;
            }
            var clock = ampm + " " +
                replaceDigitsWithDevanagari(hours.toString()) + ":" +
                replaceDigitsWithDevanagari(minutes.toString()) + ":" +
                replaceDigitsWithDevanagari(seconds.toString());

            $("#clock-container").html(clock);
        }
    };

    setInterval(extra.nepaliClock, 1000);

    function replaceDigitsWithDevanagari(str) {
        if (typeof str === 'string') {
            const devanagariDigits = ['०', '१', '२', '३', '४', '५', '६', '७', '८', '९'];
            return str.replace(/[0-9]/g, function (match) {
                return devanagariDigits[parseInt(match)];
            });
        } else {
            return str; // Return non-string values as is
        }
    }
})(jQuery);
